create table contato (id_contato bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), email varchar(255) not null, nome varchar(255) not null, observacao varchar(255) not null, status varchar(255), telefones varchar(255) not null, tipo_contato varchar(255), id_integrador bigint, id_operadora bigint, primary key (id_contato));
create table grupo (id_grupo bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), descricao varchar(255) not null, nome varchar(255) not null, primary key (id_grupo));
create table grupo_permissao (id_grupo bigint not null, id_permissao bigint not null, primary key (id_grupo, id_permissao));
create table historico (id_historico integer not null, acao varchar(255), conteudo clob, data_alteracao timestamp, nome_usuario varchar(255), tela varchar(255), usuario_id bigint, primary key (id_historico));
create table integrador (id_integrador bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), apelido varchar(255), nome varchar(255) not null, primary key (id_integrador));
create table modulo (id_modulo bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), nome varchar(255) not null, status varchar(255), primary key (id_modulo));
create table operadora (id_operadora bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), nome varchar(255) not null, status varchar(255), primary key (id_operadora));
create table parametro_operadora (id_param_o bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), descricao varchar(255) not null, nome varchar(255) not null, tamanho_maximo integer not null check (tamanho_maximo>=1), tamanho_minimo integer not null check (tamanho_minimo>=1), tipo varchar(255) not null, id_operadora bigint, primary key (id_param_o));
create table parametro_parceiro_operadora (id_param_po bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), valor varchar(255) not null, id_param_o bigint not null, id_po bigint not null, primary key (id_param_po));
create table parceiro (id_parceiro bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), cnpj varchar(255), codigo_origem varchar(255), codigo_rede varchar(255), nome varchar(255) not null, razao_social varchar(255) not null, status varchar(255), id_modulo bigint not null, primary key (id_parceiro));
create table parceiro_operadora (id_po bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), status varchar(255), id_operadora bigint not null, id_parceiro bigint not null, primary key (id_po));
create table parceiro_operadora_integrador (id_poi bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), comentario varchar(255), data timestamp, status varchar(255), trac varchar(255), id_integrador bigint not null, id_po bigint not null, primary key (id_poi));
create table permissao (id_permissao bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), descricao varchar(255) not null, nome varchar(255) not null, url varchar(255) not null, primary key (id_permissao));
create table usuario (id_usuario bigint not null, data_alteracao timestamp, data_criacao timestamp, ultimo_usuario varchar(255), usuario varchar(255), login varchar(255) not null, nome varchar(255) not null, status varchar(255), primary key (id_usuario));
create table usuario_grupo (id_usuario bigint not null, id_grupo bigint not null, primary key (id_grupo, id_usuario));